        cardList.Add(new Card(8, "Brienne of Tarth", 11, 1, "Se une a la Guardia Arcoiris de Renly Baratheon luego de derrotar a Ser Loras Tyrell en combate singular. Escolta a Catelyn Stark luego de que Stannis asesinara a Renly con magia negra. Su espada, Oathkeeper, es un testamento de su compromiso con su deber.", "Plata", "Esta carta no tiene ninguna abilidad especial.", Resources.Load<Sprite>("Brienne"), Resources.Load<Sprite>("Sword"), Resources.Load<Sprite>("Empty Image")));
