        cardList.Add(new Card(12, "Niebla Cegadora", 0, 0, "Una niebla oscura y densa se cierne sobre el campo de batalla, envolviendo a los arqueros enemigos en un manto asfixiante. Su visión se ve limitada y su precisión comprometida, mientras luchan por discernir los blancos a través de la densa neblina.", "Clima", "Reduce a 1 el poder de todas las unidades de Combate a Distancia de ambos jugadores.", Resources.Load<Sprite>("Fog"), Resources.Load<Sprite>("Empty Image"), Resources.Load<Sprite>("FogIcon")));
