        cardList.Add(new Card(15, "Cuerno Legendario", 0, 4, "Un cuerno misterioso con inscripciones antiguas. Muchos dicen que es el Cuerno del Invierno, otros lo cren el Cuerno del Dragón. Solo se conoce que al hacerlo sonar estremece el campo de batalla, infundiendo un fervor indomable en las tropas.", "Aumento", "Duplica el poder de todas las unidades de la fila. Sólo se puede jugar una carta de aumento por ronda.", Resources.Load<Sprite>("Buff"), Resources.Load<Sprite>("Empty Image"), Resources.Load<Sprite>("Horn")));
